import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  List,
  ListItem,
  ListItemText,
  ListItemSecondary,
  Chip,
  Divider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Snackbar,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  Edit as EditIcon,
  Refresh as RefreshIcon,
  Send as SendIcon,
  Mail as MailIcon,
  Drafts as DraftsIcon,
  Archive as ArchiveIcon,
} from '@mui/icons-material';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000';

function EmailManagement() {
  const [emails, setEmails] = useState([]);
  const [templates, setTemplates] = useState([]);
  const [selectedEmail, setSelectedEmail] = useState(null);
  const [openCompose, setOpenCompose] = useState(false);
  const [openTemplate, setOpenTemplate] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const [newEmail, setNewEmail] = useState({
    to: '',
    subject: '',
    body: '',
    template: '',
  });

  const [newTemplate, setNewTemplate] = useState({
    name: '',
    subject: '',
    body: '',
    variables: [],
  });

  useEffect(() => {
    fetchEmails();
    fetchTemplates();
  }, []);

  const fetchEmails = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/emails`);
      setEmails(response.data);
    } catch (error) {
      console.error('Error fetching emails:', error);
      setError('Failed to fetch emails');
    }
  };

  const fetchTemplates = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/email-templates`);
      setTemplates(response.data);
    } catch (error) {
      console.error('Error fetching templates:', error);
      setError('Failed to fetch email templates');
    }
  };

  const handleSendEmail = async () => {
    try {
      setLoading(true);
      await axios.post(`${API_BASE_URL}/send-email`, newEmail);
      setSuccess('Email sent successfully');
      setOpenCompose(false);
      setNewEmail({ to: '', subject: '', body: '', template: '' });
      fetchEmails();
    } catch (error) {
      console.error('Error sending email:', error);
      setError('Failed to send email');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveTemplate = async () => {
    try {
      setLoading(true);
      await axios.post(`${API_BASE_URL}/email-templates`, newTemplate);
      setSuccess('Template saved successfully');
      setOpenTemplate(false);
      setNewTemplate({ name: '', subject: '', body: '', variables: [] });
      fetchTemplates();
    } catch (error) {
      console.error('Error saving template:', error);
      setError('Failed to save template');
    } finally {
      setLoading(false);
    }
  };

  const handleTemplateSelect = (templateId) => {
    const template = templates.find(t => t.id === templateId);
    if (template) {
      setNewEmail({
        ...newEmail,
        template: templateId,
        subject: template.subject,
        body: template.body,
      });
    }
  };

  return (
    <Box>
      <Snackbar open={!!error} autoHideDuration={6000} onClose={() => setError('')}>
        <Alert onClose={() => setError('')} severity="error">
          {error}
        </Alert>
      </Snackbar>

      <Snackbar open={!!success} autoHideDuration={6000} onClose={() => setSuccess('')}>
        <Alert onClose={() => setSuccess('')} severity="success">
          {success}
        </Alert>
      </Snackbar>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          Email Management
        </Typography>
        <Box>
          <Button
            variant="contained"
            color="primary"
            onClick={() => setOpenTemplate(true)}
            startIcon={<AddIcon />}
            sx={{ mr: 2 }}
          >
            New Template
          </Button>
          <Button
            variant="contained"
            color="primary"
            onClick={() => setOpenCompose(true)}
            startIcon={<SendIcon />}
          >
            Compose
          </Button>
        </Box>
      </Box>

      <Grid container spacing={3}>
        {/* Email Templates */}
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Email Templates
              </Typography>
              <List>
                {templates.map((template) => (
                  <ListItem
                    key={template.id}
                    secondaryAction={
                      <IconButton edge="end" onClick={() => handleTemplateSelect(template.id)}>
                        <EditIcon />
                      </IconButton>
                    }
                  >
                    <ListItemText
                      primary={template.name}
                      secondary={`Variables: ${template.variables.join(', ')}`}
                    />
                  </ListItem>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>

        {/* Recent Emails */}
        <Grid item xs={12} md={8}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Recent Emails
              </Typography>
              <List>
                {emails.map((email) => (
                  <ListItem
                    key={email.id}
                    secondaryAction={
                      <Box>
                        <Chip
                          label={email.status}
                          color={email.status === 'sent' ? 'success' : 'default'}
                          size="small"
                          sx={{ mr: 1 }}
                        />
                        <IconButton edge="end" onClick={() => setSelectedEmail(email)}>
                          <MailIcon />
                        </IconButton>
                      </Box>
                    }
                  >
                    <ListItemText
                      primary={email.subject}
                      secondary={`To: ${email.to} | ${new Date(email.date).toLocaleString()}`}
                    />
                  </ListItem>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Compose Email Dialog */}
      <Dialog
        open={openCompose}
        onClose={() => setOpenCompose(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Compose Email</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Template</InputLabel>
              <Select
                value={newEmail.template}
                onChange={(e) => handleTemplateSelect(e.target.value)}
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {templates.map((template) => (
                  <MenuItem key={template.id} value={template.id}>
                    {template.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField
              label="To"
              value={newEmail.to}
              onChange={(e) => setNewEmail({ ...newEmail, to: e.target.value })}
              fullWidth
              required
            />

            <TextField
              label="Subject"
              value={newEmail.subject}
              onChange={(e) => setNewEmail({ ...newEmail, subject: e.target.value })}
              fullWidth
              required
            />

            <TextField
              label="Body"
              value={newEmail.body}
              onChange={(e) => setNewEmail({ ...newEmail, body: e.target.value })}
              fullWidth
              multiline
              rows={6}
              required
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenCompose(false)}>Cancel</Button>
          <Button
            onClick={handleSendEmail}
            variant="contained"
            color="primary"
            disabled={loading}
            startIcon={loading ? <CircularProgress size={20} /> : <SendIcon />}
          >
            Send
          </Button>
        </DialogActions>
      </Dialog>

      {/* New Template Dialog */}
      <Dialog
        open={openTemplate}
        onClose={() => setOpenTemplate(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Create Email Template</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
            <TextField
              label="Template Name"
              value={newTemplate.name}
              onChange={(e) => setNewTemplate({ ...newTemplate, name: e.target.value })}
              fullWidth
              required
            />

            <TextField
              label="Subject"
              value={newTemplate.subject}
              onChange={(e) => setNewTemplate({ ...newTemplate, subject: e.target.value })}
              fullWidth
              required
            />

            <TextField
              label="Body"
              value={newTemplate.body}
              onChange={(e) => setNewTemplate({ ...newTemplate, body: e.target.value })}
              fullWidth
              multiline
              rows={6}
              required
              helperText="Use {{variable_name}} for template variables"
            />

            <TextField
              label="Variables"
              value={newTemplate.variables.join(', ')}
              onChange={(e) => setNewTemplate({
                ...newTemplate,
                variables: e.target.value.split(',').map(v => v.trim()).filter(Boolean)
              })}
              fullWidth
              helperText="Comma-separated list of variable names"
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenTemplate(false)}>Cancel</Button>
          <Button
            onClick={handleSaveTemplate}
            variant="contained"
            color="primary"
            disabled={loading}
            startIcon={loading ? <CircularProgress size={20} /> : <AddIcon />}
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>

      {/* View Email Dialog */}
      <Dialog
        open={!!selectedEmail}
        onClose={() => setSelectedEmail(null)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>View Email</DialogTitle>
        <DialogContent>
          {selectedEmail && (
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
              <Typography variant="subtitle2">To: {selectedEmail.to}</Typography>
              <Typography variant="subtitle2">
                Date: {new Date(selectedEmail.date).toLocaleString()}
              </Typography>
              <Typography variant="subtitle2">Status: {selectedEmail.status}</Typography>
              <Divider />
              <Typography variant="h6">{selectedEmail.subject}</Typography>
              <Typography>{selectedEmail.body}</Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedEmail(null)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default EmailManagement;
